import React, { useState } from "react";

const ENV = {
  API: "https://www.google.com"
};

export default function CreateDogAccount() {
  const [name, setName] = useState("");
  const [breed, setBreed] = useState("");
  const [age, setAge] = useState("");
  const [size, setSize] = useState("");
  const [additionalComments, setAdditionalComments] = useState("");

  const createDogAccount = () => {
    const body = {
      name,
      breed,
      age,
      size,
      additionalComments
    };
    fetch(ENV.API + "/user", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: body
    });
  };

  return (
    <div>
      <h1>Create Dog Account</h1>

      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={event => setName(event.target.value)}
      />
      <input
        type="text"
        placeholder="Breed"
        value={breed}
        onChange={event => setBreed(event.target.value)}
      />
      <input
        type="text"
        placeholder="Age"
        value={age}
        onChange={event => setAddress(event.target.value)}
      />
      <input
        type="text"
        placeholder="Size"
        value={size}
        onChange={event => setSize(event.target.value)}
      />
      <input
        type="text"
        placeholder="Additional Comments"
        value={additionalComments}
        onChange={event => setAdditionalComments(event.target.value)}
      />

      <button onClick={createDogAccount}>Create Dog Account</button>
    </div>
  );
}
